mapper.select:
select t.receivable_amount,t.receivable_amount receivableAmount,t.prepayment_id,t.prepayment_id
prepaymentId,t.update_time,t.update_time updateTime,t.remark,t.status_cd,t.status_cd
statusCd,t.obj_name,t.obj_name objName,t.received_amount,t.received_amount receivedAmount,
t.fee_id,t.fee_id feeId,t.config_id,t.config_id configId,t.obj_id,t.obj_id objId,t.fee_name,
t.fee_name feeName,t.owe_amount,t.owe_amount oweAmount,t.community_id,t.community_id communityId,
t.obj_type,t.obj_type objType,t.obj_name_num,t.bill_state,t.bill_state billState,
t.obj_name_num objNameNum,t.detail_id,t.detail_id prepaymentDetailId,
t.pay_time,t.pay_time payTime,t.prepayment_state,t.prepayment_state prepaymentState,
t.fee_begin_time,t.fee_begin_time feeBeginTime,t.fee_finish_time,t.fee_finish_time feeFinishTime,
t.prepayment_cycle,t.prepayment_cycle prepaymentCycle,t.pay_fee_flag,t.pay_fee_flag payFeeFlag,
t.payable_amount,t.payable_amount payableAmount
from report_fee_month_statistics_bill t
inner join pay_fee pf on t.fee_id = pf.fee_id and pf.community_id = t.community_id and pf.status_cd = '0'
where 1 =1

    and t.receivable_amount= #{receivableAmount}


    and t.prepayment_id= #{prepaymentId}


    and t.update_time= #{updateTime}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.obj_name= #{objName}


    and t.obj_name_num = #{objNameNum}


    and t.received_amount= #{receivedAmount}


    and t.fee_id= #{feeId}


    and t.config_id= #{configId}


    and t.obj_id= #{objId}


    and t.fee_name= #{feeName}


    and t.owe_amount= #{oweAmount}


    and t.community_id= #{communityId}


    and t.obj_type= #{objType}


    and t.pay_time= #{payTime}


    and t.prepayment_state= #{prepaymentState}


    and t.bill_state= #{billState}


    and t.fee_begin_time= #{feeBeginTime}


    and t.fee_finish_time= #{feeFinishTime}


    and t.prepayment_cycle= #{prepaymentCycle}


    and t.pay_fee_flag= #{payFeeFlag}


    and t.detail_id= #{prepaymentDetailId}


    and t.payable_amount= #{payableAmount}


    and t.fee_finish_time <= #{nextStartTime}


    and t.fee_begin_time >= #{newBeginTime}


    and t.fee_finish_time <= #{newFinishTime}

order by t.create_time desc

    limit #{page}, #{row}

select t.config_id configId
from report_fee_month_statistics_bill t
left join pay_fee pf on t.fee_id = pf.fee_id and pf.community_id = t.community_id and pf.status_cd = '0'
left join pay_fee_detail pfd on t.detail_id = pfd.detail_id and pfd.status_cd = '0'
left join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0'
left join building_room br on pf.payer_obj_id = br.room_id and pf.payer_obj_type = '3333' and br.status_cd = '0'
left join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0'
left join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0'
left join owner_car oc on pf.payer_obj_id = oc.car_id and oc.car_type_cd='1001'
left join parking_space ps on oc.ps_id = ps.ps_id and ps.status_cd = '0'
left join parking_area pa on ps.pa_id = pa.pa_id and pa.status_cd = '0'
LEFT JOIN contract co on pf.payer_obj_id = co.contract_id and co.status_cd = '0' and pf.payer_obj_type='7777'
LEFT JOIN c_business cb on pfd.b_id = cb.b_id
where 1 =1 and t.status_cd = '0'

    and br.room_num= #{roomNum}


    and bu.unit_id= #{unitId}


    and bu.unit_num= #{unitNum}


    and f.floor_id = #{floorId}


    and f.floor_num = #{floorNum}


    and pfd.obj_name= #{objName}


    and t.fee_id= #{feeId}


    and pfc.config_id= #{configId}


    and pf.payer_obj_id= #{objId}


    and pfc.fee_name= #{feeName}


    and t.community_id= #{communityId}


    and pfd.prime_rate = #{primeRate}


    and pfd.state = #{state}


    and t.prepayment_state = #{prepaymentState}


    and t.bill_state = #{billState}


    and pfc.fee_type_cd = #{feeTypeCd}


    and t.pay_time >= #{startTime}


    and t.pay_time <= #{endTime}


    and t.fee_begin_time >= #{startBeginTime}


    and t.fee_begin_time <= #{startFinishTime}


    and t.fee_finish_time >= #{endBeginTime}


    and t.fee_finish_time <= #{endFinishTime}

group by t.config_id

    limit #{page}, #{row}

select SUM(t.payableAmount) allPayableAmount,SUM(t.receivableAmount) allReceivableAmount,
SUM(t.receivedAmount) allReceivedAmount,SUM(t.oweAmount) allOweAmount FROM
(select t.config_id configId,SUM(t.payable_amount) payableAmount,SUM(t.receivable_amount) receivableAmount,
SUM(t.received_amount) receivedAmount,SUM(t.owe_amount) oweAmount
from report_fee_month_statistics_bill t
left join pay_fee pf on t.fee_id = pf.fee_id and pf.community_id = t.community_id and pf.status_cd = '0'
left join pay_fee_detail pfd on t.detail_id = pfd.detail_id and pfd.status_cd = '0'
left join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0'
left join building_room br on pf.payer_obj_id = br.room_id and pf.payer_obj_type = '3333' and br.status_cd = '0'
left join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0'
left join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0'
left join owner_car oc on pf.payer_obj_id = oc.car_id and oc.car_type_cd='1001'
left join parking_space ps on oc.ps_id = ps.ps_id and ps.status_cd = '0'
left join parking_area pa on ps.pa_id = pa.pa_id and pa.status_cd = '0'
left join pay_fee_attrs pfa on t.fee_id = pfa.fee_id and pfa.spec_cd = '390002'
left join pay_fee_attrs pfb on t.fee_id = pfb.fee_id and pfb.spec_cd = '390007'
left join pay_fee_attrs pfo on t.fee_id = pfo.fee_id and pfo.spec_cd = '390008'
left join pay_fee_attrs pft on t.fee_id = pft.fee_id and pft.spec_cd = '390001'
LEFT JOIN contract co on pf.payer_obj_id = co.contract_id and co.status_cd = '0' and pf.payer_obj_type='7777'
LEFT JOIN c_business cb on pfd.b_id = cb.b_id
where 1 =1 and t.status_cd = '0'

    and br.room_num= #{roomNum}


    and bu.unit_id= #{unitId}


    and bu.unit_num= #{unitNum}


    and f.floor_id = #{floorId}


    and f.floor_num = #{floorNum}


    and pfd.obj_name= #{objName}


    and t.fee_id= #{feeId}


    and t.config_id= #{configId}


    and pf.payer_obj_id= #{objId}


    and pfc.fee_name= #{feeName}


    and t.community_id= #{communityId}


    and pfd.prime_rate = #{primeRate}


    and pfd.state = #{state}


    and t.prepayment_state = #{prepaymentState}


    and t.bill_state = #{billState}


    and pfc.fee_type_cd = #{feeTypeCd}


    and t.pay_time >= #{startTime}


    and t.pay_time <= #{endTime}


    and t.fee_begin_time >= #{startBeginTime}


    and t.fee_begin_time <= #{startFinishTime}


    and t.fee_finish_time >= #{endBeginTime}


    and t.fee_finish_time <= #{endFinishTime}

group by t.config_id

    limit #{page}, #{row}

) t

select t.discountSmallType discountSmallType,SUM(t.discountPrice) discountPrice from
(select t.config_id configId,fdr.discount_small_type discountSmallType,SUM(pfdd.discount_price) discountPrice
from report_fee_month_statistics_bill t
left join pay_fee pf on t.fee_id = pf.fee_id and pf.community_id = t.community_id and pf.status_cd = '0'
left join pay_fee_detail pfd on t.detail_id = pfd.detail_id and pfd.status_cd = '0'
left join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0'
left join building_room br on pf.payer_obj_id = br.room_id and pf.payer_obj_type = '3333' and br.status_cd = '0'
left join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0'
left join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0'
left join owner_car oc on pf.payer_obj_id = oc.car_id and oc.car_type_cd='1001'
left join parking_space ps on oc.ps_id = ps.ps_id and ps.status_cd = '0'
left join parking_area pa on ps.pa_id = pa.pa_id and pa.status_cd = '0'
left join pay_fee_attrs pfa on t.fee_id = pfa.fee_id and pfa.spec_cd = '390002'
left join pay_fee_attrs pfb on t.fee_id = pfb.fee_id and pfb.spec_cd = '390007'
left join pay_fee_attrs pfo on t.fee_id = pfo.fee_id and pfo.spec_cd = '390008'
left join pay_fee_attrs pft on t.fee_id = pft.fee_id and pft.spec_cd = '390001'
LEFT JOIN contract co on pf.payer_obj_id = co.contract_id and co.status_cd = '0' and pf.payer_obj_type='7777'
LEFT JOIN c_business cb on pfd.b_id = cb.b_id
left join pay_fee_detail_discount pfdd on t.detail_id = pfdd.detail_id and pfdd.status_cd = '0'
left join fee_discount fd on fd.discount_id = pfdd.discount_id and fd.status_cd = '0'
left join fee_discount_rule fdr on fd.rule_id = fdr.rule_id and fdr.status_cd = '0'
where 1 =1 and t.status_cd = '0'

    and br.room_num= #{roomNum}


    and bu.unit_id= #{unitId}


    and bu.unit_num= #{unitNum}


    and f.floor_id = #{floorId}


    and f.floor_num = #{floorNum}


    and pfd.obj_name= #{objName}


    and t.fee_id= #{feeId}


    and t.config_id= #{configId}


    and pf.payer_obj_id= #{objId}


    and pfc.fee_name= #{feeName}


    and t.community_id= #{communityId}


    and pfd.prime_rate = #{primeRate}


    and pfd.state = #{state}


    and t.prepayment_state = #{prepaymentState}


    and t.bill_state = #{billState}


    and pfc.fee_type_cd = #{feeTypeCd}


    and t.pay_time >= #{startTime}


    and t.pay_time <= #{endTime}


    and t.fee_begin_time >= #{startBeginTime}


    and t.fee_begin_time <= #{startFinishTime}


    and t.fee_finish_time >= #{endBeginTime}


    and t.fee_finish_time <= #{endFinishTime}

group by t.config_id,fdr.discount_small_type

    limit #{page}, #{row}

) t where t.discountSmallType is not null group by t.discountSmallType

mapper.update:
update report_fee_month_statistics_bill t set
t.owe_amount= #{oweAmount}
where 1=1

    and t.prepayment_id= #{prepaymentId}


    and t.fee_id= #{feeId}

