    ,t.b_id = #{newBId}


    , t.receivable_amount= #{receivableAmount}


    , t.update_time= #{updateTime}


    , t.remark= #{remark}


    , t.obj_name= #{objName}


    , t.obj_name_num= #{objNameNum}


    , t.received_amount= #{receivedAmount}


    , t.fee_id= #{feeId}


    , t.config_id= #{configId}


    , t.obj_id= #{objId}


    , t.fee_name= #{feeName}


    , t.owe_amount= #{oweAmount}


    , t.payable_amount= #{payableAmount}


    , t.obj_type= #{objType}


    , t.pay_time= #{payTime}


