    , t.fee_finish_time= #{feeFinishTime}


    , t.prepayment_cycle= #{prepaymentCycle}


    , t.pay_fee_flag= #{payFeeFlag}


    , t.detail_id= #{prepaymentDetailId}

where 1=1
and t.prepayment_id= #{prepaymentId}

    and t.community_id= #{communityId}

