    and t.prepayment_id= #{prepaymentId}


    and t.community_id= #{communityId}


    and t.fee_id= #{feeId}


    and t.config_id= #{configId}

mapper.select:
select count(1) count
from report_fee_month_statistics_bill t
inner join pay_fee pf on t.fee_id = pf.fee_id and pf.community_id = t.community_id and pf.status_cd = '0'
where 1 =1

    and t.receivable_amount= #{receivableAmount}


    and t.prepayment_id= #{prepaymentId}


    and t.update_time= #{updateTime}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.obj_name= #{objName}


    and t.obj_name_num= #{objNameNum}


    and t.received_amount= #{receivedAmount}


    and t.fee_id= #{feeId}


    and t.config_id= #{configId}


    and t.obj_id= #{objId}


    and t.fee_name= #{feeName}


    and t.owe_amount= #{oweAmount}


    and t.community_id= #{communityId}


    and t.payable_amount= #{payableAmount}


    and t.obj_type= #{objType}


    and t.pay_time= #{payTime}


    and t.prepayment_state= #{prepaymentState}


    and t.bill_state= #{billState}


    and t.fee_begin_time= #{feeBeginTime}


    and t.fee_finish_time= #{feeFinishTime}


    and t.prepayment_cycle= #{prepaymentCycle}


    and t.pay_fee_flag= #{payFeeFlag}


    and t.detail_id= #{prepaymentDetailId}

select count(1) count
from report_fee_month_statistics_bill t
left join building_room br on t.obj_id = br.room_id and t.obj_type = '3333' and br.status_cd = '0'
left join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0'
left join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0'
inner join pay_fee pf on t.fee_id = pf.fee_id and pf.community_id = t.community_id and pf.status_cd = '0'
where t.status_cd = '0'

    and br.room_num= #{roomNum}


    and bu.unit_id= #{unitId}


    and bu.unit_id in

        #{item}



    and f.floor_id = #{floorId}


    and t.obj_name= #{objName}


    and t.obj_name_num= #{objNameNum}


    and t.fee_id= #{feeId}


    and t.config_id= #{configId}


    and t.config_id in

        #{item}



    and t.obj_id= #{objId}


    and t.pay_time= #{payTime}


    and t.prepayment_state= #{prepaymentState}


    and t.bill_state= #{billState}


    and t.fee_begin_time= #{feeBeginTime}


    and t.fee_finish_time= #{feeFinishTime}


    and t.prepayment_cycle= #{prepaymentCycle}


    and t.pay_fee_flag= #{payFeeFlag}


    and t.detail_id= #{prepaymentDetailId}


    and t.fee_name= #{feeName}


    and t.community_id= #{communityId}


    and t.create_time >= #{startTime}


    and t.create_time <= #{endTime}

select count(1) count
from (
select t.payer_obj_name objName,t.fee_name feeName,pf.end_time feeCreateTime,t.create_time
createTime,SUM(t.amount_owed) oweAmount,max(t.deadline_time) deadlineTime
from report_owe_fee t
inner join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0'
left join building_room br on t.payer_obj_id = br.room_id and t.payer_obj_type = '3333' and br.status_cd = '0'
left join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0'
left join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0'
inner join pay_fee pf on t.fee_id = pf.fee_id and pf.community_id = t.community_id and pf.status_cd = '0'
where 1=1

    and br.room_num= #{roomNum}


    and bu.unit_id= #{unitId}


    and f.floor_id = #{floorId}


    and t.payer_obj_name= #{objName}


    and t.fee_id= #{feeId}


    and t.config_id= #{configId}


    and t.payer_obj_id= #{objId}


    and t.fee_name= #{feeName}


    and t.community_id= #{communityId}


    and t.create_time >= #{startTime}


    and t.create_time <= #{endTime}

and t.amount_owed > 0 and pf.end_time < now()
group by t.payer_obj_name ,t.fee_name ,pf.end_time ,t.create_time
HAVING oweAmount > 0
) t

select t.payer_obj_name objName,t.fee_name feeName,pf.end_time feeCreateTime,t.update_time
updateTime,pfa.`value` ownerName,br.built_up_area builtUpArea ,SUM(t.amount_owed) oweAmount,max(t.deadline_time)
deadlineTime
from report_owe_fee t
inner join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0'
left join building_room br on t.payer_obj_id = br.room_id and t.payer_obj_type = '3333' and br.status_cd = '0'
left join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0'
left join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0'
inner join pay_fee pf on t.fee_id = pf.fee_id and pf.community_id = t.community_id and pf.status_cd = '0'
left join pay_fee_attrs pfa on pf.fee_id = pfa.fee_id and pfa.spec_cd = '390008' and pfa.status_cd ='0'
where 1=1

    and br.room_num= #{roomNum}


    and bu.unit_id= #{unitId}


    and bu.unit_num= #{unitNum}


    and f.floor_id = #{floorId}


    and f.floor_num = #{floorNum}


    and t.payer_obj_name= #{objName}


    and t.fee_id= #{feeId}


    and t.config_id= #{configId}


    and t.payer_obj_id= #{objId}


    and t.fee_name= #{feeName}


    and t.community_id= #{communityId}


    and t.create_time >= #{startTime}


    and t.create_time <= #{endTime}

and t.amount_owed > 0 and pf.end_time < now()
group by t.payer_obj_name ,t.fee_name ,pf.end_time ,t.update_time
updateTime,pfa.`value` ,br.built_up_area
HAVING oweAmount > 0
order by t.payer_obj_name

    limit #{page}, #{row}

select SUM(t.amount_owed) oweAmount
from report_owe_fee t
inner join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0'
left join building_room br on t.payer_obj_id = br.room_id and t.payer_obj_type = '3333' and br.status_cd = '0'
left join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0'
left join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0'
inner join pay_fee pf on t.fee_id = pf.fee_id and pf.community_id = t.community_id and pf.status_cd = '0'
where 1=1

    and br.room_num= #{roomNum}


    and bu.unit_id= #{unitId}


    and f.floor_id = #{floorId}


    and t.payer_obj_name= #{objName}


    and t.fee_id= #{feeId}


    and t.config_id= #{configId}


    and t.payer_obj_id= #{objId}


    and t.fee_name= #{feeName}


    and t.community_id= #{communityId}


    and t.create_time >= #{startTime}


    and t.create_time <= #{endTime}

select count(1) count,SUM(receivedAmount) totalReceivedAmount,SUM(receivableAmount) totalReceivableAmount
from (
select pfc.fee_name feeName,f.floor_num floorNum,bu.unit_num unitNum,br.room_num roomNum,br.built_up_area
builtUpArea ,oc.car_num carNum,pf.payer_obj_type payerObjType,pfd.start_time startTime,pfd.end_time
endTime,pfd.create_time createTime,t.prepayment_id,t.prepayment_id prepaymentId,t.obj_type,t.obj_type objType,
t.obj_id,t.obj_id objId,t.obj_name,t.obj_name objName,t.config_id,t.config_id configId,
t.receivable_amount,t.receivable_amount prepaymentReceivableAmount,t.received_amount,t.received_amount
prepaymentReceivedAmount,t.owe_amount,t.owe_amount oweAmount,t.remark,t.bill_state,t.bill_state
billState,td1.name billStateName,t.update_time,t.update_time updateTime,t.obj_name_num,t.obj_name_num
objNameNum,t.pay_time,t.pay_time payTime,t.prepayment_state,t.prepayment_state prepaymentState,td.name
prepaymentStateName,t.fee_begin_time,
t.fee_begin_time feeBeginTime,t.fee_finish_time,t.fee_finish_time feeFinishTime,
t.prepayment_cycle,t.prepayment_cycle prepaymentCycle,t.pay_fee_flag,t.pay_fee_flag
payFeeFlag,t.detail_id,t.detail_id prepaymentDetailId,t.payable_amount,t.payable_amount payableAmount,
pfd.receivable_amount receivableAmount,pfd.received_amount receivedAmount,pfa.`value` importFeeName,
pfd.prime_rate,d.name primeRate,co.contract_code contractCode,pfo.`value` ownerName,cb.o_id oId,
d2.name feeTypeCdName,pfc.fee_type_cd feeTypeCd,pfd.state,d3.name stateName,t.fee_id feeId,pft.`value` repairId,
pfd.detail_id detailId,pf.payer_obj_id,pfb.value ownerId,concat(pa.num,'停车场',ps.num,'车位') psName
from report_fee_month_statistics_bill t
left join t_dict td on t.prepayment_state = td.status_cd and td.table_name = 'report_fee_month_statistics_bill'
and td.table_columns = 'prepayment_state'
left join t_dict td1 on t.bill_state = td1.status_cd and td1.table_name = 'report_fee_month_statistics_bill'
and td1.table_columns = 'bill_state'
left join pay_fee pf on t.fee_id = pf.fee_id and pf.status_cd = '0'
left join pay_fee_detail pfd on t.detail_id = pfd.detail_id and pfd.status_cd = '0'
left join pay_fee_config pfc on t.config_id = pfc.config_id and pfc.status_cd = '0'
left join building_room br on pf.payer_obj_id = br.room_id and pf.payer_obj_type = '3333' and br.status_cd = '0'
left join building_unit bu on br.unit_id = bu.unit_id and bu.status_cd = '0'
left join f_floor f on bu.floor_id = f.floor_id and f.status_cd = '0'
left join owner_car oc on pf.payer_obj_id = oc.car_id and oc.car_type_cd='1001'
left join parking_space ps on oc.ps_id = ps.ps_id and ps.status_cd = '0'
left join parking_area pa on ps.pa_id = pa.pa_id and pa.status_cd = '0'
left join pay_fee_attrs pfa on t.fee_id = pfa.fee_id and pfa.spec_cd = '390002'
left join pay_fee_attrs pfb on t.fee_id = pfb.fee_id and pfb.spec_cd = '390007'
left join pay_fee_attrs pfo on t.fee_id = pfo.fee_id and pfo.spec_cd = '390008'
left join pay_fee_attrs pft on t.fee_id = pft.fee_id and pft.spec_cd = '390001'
left join t_dict d on pfd.prime_rate = d.status_cd and d.table_name="pay_fee_detail" and
d.table_columns="prime_rate"
left join t_dict d2 on pfc.fee_type_cd = d2.status_cd and d2.table_name = "pay_fee_config" and d2.table_columns
= "fee_type_cd"
