select count(1) count,SUM(receivedAmount) totalReceivedAmount,SUM(receivableAmount) totalReceivableAmount,
SUM(payableAmount) totalPayableAmount,SUM(oweAmount) totalOweAmount
from (
select pfc.fee_name feeName,pfc.fee_type_cd feeTypeCd,td.name feeTypeCdName,
fdr.discount_small_type discountSmallType,fdr.rule_name ruleName,
SUM(t.payable_amount) payableAmount,SUM(t.receivable_amount) receivableAmount,
SUM(t.received_amount) receivedAmount,SUM(t.owe_amount) oweAmount
from report_fee_month_statistics_bill t
left join pay_fee_config pfc on pfc.config_id = t.config_id and pfc.status_cd = '0'
left join t_dict td on td.status_cd = pfc.fee_type_cd and td.table_name = 'pay_fee_config' and td.table_columns
= 'fee_type_cd'
left join pay_fee_config_discount pfcd on t.config_id = pfcd.config_id and pfcd.status_cd = '0'
left join fee_discount fd on pfcd.discount_id = fd.discount_id and fd.status_cd = '0'
left join fee_discount_rule fdr on fd.rule_id = fdr.rule_id and fdr.status_cd = '0'
where t.status_cd = '0'

    and pfc.config_id= #{configId}


    and pf.payer_obj_id= #{objId}


    and pfc.fee_name= #{feeName}


    and t.community_id= #{communityId}


    and pfd.prime_rate = #{primeRate}


    and pfd.state = #{state}


    and t.prepayment_state = #{prepaymentState}


    and t.bill_state = #{billState}


    and pfc.fee_type_cd = #{feeTypeCd}


    and t.pay_time >= #{startTime}


    and t.pay_time <= #{endTime}


    and t.fee_begin_time >= #{startBeginTime}


    and t.fee_begin_time <= #{startFinishTime}


    and t.fee_finish_time >= #{endBeginTime}


    and t.fee_finish_time <= #{endFinishTime}

group by t.config_id) t

