mapper.select:
select t.apply_id,t.apply_id applyId,t.create_user_id,t.create_user_id
createUserId,t.remark,t.status_cd,t.status_cd statusCd,t.state,t.owner_id,t.owner_id
ownerId,t.community_id,t.community_id communityId
from owner_settled_apply t
where 1 =1

    and t.apply_id= #{applyId}


    and t.create_user_id= #{createUserId}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.state= #{state}


    and t.owner_id= #{ownerId}


    and t.community_id= #{communityId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update owner_settled_apply t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.create_user_id= #{createUserId}


    , t.remark= #{remark}


    , t.state= #{state}


    , t.owner_id= #{ownerId}


    , t.community_id= #{communityId}

where 1=1

    and t.apply_id= #{applyId}

mapper.select:
select count(1) count
from owner_settled_apply t
where 1 =1

    and t.apply_id= #{applyId}


    and t.create_user_id= #{createUserId}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.state= #{state}


    and t.owner_id= #{ownerId}


    and t.community_id= #{communityId}
