mapper.select:
select t.score,t.value_id,t.value_id valueId,t.title_id,t.title_id titleId,t.status_cd,t.status_cd
statusCd,t.community_id,t.community_id communityId,t.qa_value,t.qa_value qaValue,t.seq
from question_title_value t
where 1 =1

    and t.score= #{score}


    and t.value_id= #{valueId}


    and t.title_id= #{titleId}


    and t.title_id in

        #{item}



    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.qa_value= #{qaValue}


    and t.seq= #{seq}

order by t.seq

    limit #{page}, #{row}

mapper.update:
update question_title_value t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.score= #{score}


    , t.qa_value= #{qaValue}


    , t.seq= #{seq}

where 1=1

    and t.value_id= #{valueId}


    and t.title_id= #{titleId}


    and t.community_id= #{communityId}

mapper.select:
select count(1) count
from question_title_value t
where 1 =1

    and t.score= #{score}


    and t.value_id= #{valueId}


    and t.title_id= #{titleId}


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.qa_value= #{qaValue}


    and t.seq= #{seq}

select qt.qa_title qaTitle,qt.title_id titleId, qt.title_type titleType,qa.qa_id qaId,t.qa_value qaValue,t.seq,count(uqav.user_qa_id) personCount from question_title_value t inner join question_title qt on t.title_id = qt.title_id and qt.status_cd = '0' inner join question_answer_title_rel qatr on t.title_id = qatr.title_id and qatr.status_cd = '0' inner join question_answer qa on qatr.qa_id = qa.qa_id and qa.status_cd = '0' left join user_question_answer_value uqav on qa.qa_id = uqav.qa_id and t.value_id = uqav.value_id and t.title_id = uqav.title_id and uqav.status_cd = '0' where 1=1
and t.community_id = #{communityId}
and t.title_id= #{titleId}
and t.status_cd= #{statusCd}
and qa.qa_id in
#{item}
group by qt.qa_title,qt.title_id , qt.title_type,qa.qa_id,t.qa_value,t.seq order by t.seq
